def length_journalists
	return $journalists.size
end

def number_journalists
	number = 0
	j = 0
	$journalists.each do |journalist|
		(0..9).each do |i|
			i = i.to_s
			if journalist.include?(i) && j == 0
				number += 1
				j = 1
#				puts journalist
			end
		end
		j = 0
	end
	return number
end 

def aude_who
	number = 0
	$journalists.each do |journalist|
		low_cap = journalist.downcase
		if low_cap.include?("aude")
			number += 1
#			puts journalist
		end
	end
	return number
end

def first_upper
	number = 0
	$journalists.each do |journalist|
		if journalist.match(/@[A-Z]/)
			number += 1
#			puts journalist
		end
	end
	return number
end

def uppercase
	number = 0
	$journalists.each do |journalist|
		if journalist.index(/[A-Z]/)
			number += 1
#			puts journalist
		end
	end
	return number
end

def underscore
	number = 0
	$journalists.each do |journalist|
		letter = journalist.split ""
		letter.each do |i|
			if i.match("_")
				number += 1
#				puts journalist
			end
		end
	end
	return number
end

def sorting
	sorted = $journalists.sort_by { |word| word.downcase }
	# 
	return sorted
end

def short
	sorted2 = $journalists.sort_by { |word| word.length }
	fifty_first = sorted2[0..49]
	return fifty_first
end

def where_epenser
	indice = $journalists.index("@epenser") + 1
	return indice
end

def journalists_list
	puts "Il y a #{length_journalists} dans la liste."
	puts "#{number_journalists} journalistes ont des chiffres dans leur handle." 
	puts "#{aude_who} journalistes contiennent 'aude' dans leur handle."
	puts "#{first_upper} journalistes ont une majuscule au début de leur handle."
	puts "#{uppercase} journalistes ont un handle contenant une majuscule."
	puts "Il y a au total #{underscore} underscore dans la liste des journalistes (certains en ont deux dans leur handle)."
	puts "Voici la liste des journalistes dans l'ordre alphabetique :"
	puts sorting
	puts "Les cinquantes handles de journalistes les plus courtes sont :"
	puts short
	puts "Le journaliste e_penser est la position #{where_epenser} de la liste."
end

